implementation.swift:
let instrument = JUCEInstrument(pluginId: "LOCAL_GAL")

// Set up morph states
instrument.setMorphPresetA(
    rubber: 0.8, bite: 0.2,
    hollow: 0.9, growl: 0.1
)

instrument.setMorphPresetB(
    rubber: 0.1, bite: 0.9,
    hollow: 0.2, growl: 0.8
)

// Automate morph position
instrument.setMorphPosition(0.0)  // Start soft

// Gradually morph to aggressive
for i in 0..<32 {
    let position = Float(i) / 32.0
    instrument.setMorphPosition(position)
    // Play notes at each morph stage
}
